mapper.select:
SELECT seq ,company ,workcenter ,category ,(SELECT code_name FROM master.common_code WHERE company IN ('COMMON',w.company) AND type = 'workcenter_category' AND code = w.category) AS category_name ,"name" ,ccr_flag ,lead_time ,worktime_factor ,grouping_period ,(SELECT COUNT(0) FROM master.workcenter_equipment WHERE workcenter_seq = w.seq) AS multi_work_count ,remark ,creator ,(SELECT "name" FROM master.user WHERE user_id = w.creator) AS creator_name ,to_char(create_date, 'YYYY-MM-DD HH24:MI') AS create_date
,((SELECT COUNT(0) FROM master.routing WHERE company = w.company AND workcenter = w.workcenter AND deletion_flag IS NULL)+ (SELECT COUNT(0) FROM master.equipment WHERE company = w.company AND workcenter = w.workcenter AND deletion_flag IS NULL)+ (SELECT COUNT(0) FROM product.shop_order_routing WHERE company = w.company AND workcenter = w.workcenter AND deletion_flag IS NULL)+ (SELECT COUNT(0) FROM product.shop_order_status WHERE company = w.company AND current_workcenter = w.workcenter AND completion_flag IS NULL)) AS use_count
FROM master.workcenter w WHERE company = #{company}
AND deletion_flag IS NULL
ORDER BY ${sort} ${sortType}
,${sort2} ${sortType2}
LIMIT ${pageSize} OFFSET (CAST(${page} AS int)-1) * ${pageSize}

INSERT INTO master.workcenter(
	company
	,workcenter
	,category
	,name
	,ccr_flag
	,lead_time
	,worktime_factor
	,grouping_period
	,remark
	,creator
	,create_date
) VALUES (
	#{company}
	,#{workcenter}
	,#{category}
	,#{name}
	,#{ccr_flag}
	,CAST(#{lead_time} AS NUMERIC)
	,CAST(#{worktime_factor} AS NUMERIC)
	,CAST(#{grouping_period} AS INTEGER)
	,#{remark}
	,#{user_id}
	,NOW()	
) RETURNING seq

mapper.update:
UPDATE 
	master.workcenter
SET 
	category = #{category}
	,"name" = #{name}
	,ccr_flag = #{ccr_flag}
	,lead_time = CAST(#{lead_time} AS NUMERIC)
	,worktime_factor = CAST(#{worktime_factor} AS NUMERIC)
	,grouping_period = CAST(#{grouping_period} AS INTEGER)
	,remark = #{remark}
	,deletion_flag = NULL
WHERE
	company = #{company}
AND workcenter = #{workcenter}
